import React from "react";
import '/home/mslm/positivus/Positivus/src/components/HeroSection.scss';
import heroImg from '/home/mslm/positivus/Positivus/src/assets/Images/HeroSection/heroImage.svg'

const HeroSection = () => {
return (
    <section className="heroSection">
        <div className="grid lg:grid-cols-2 gap-100px w-full">
            <div className="col-1">
                <h1 className="text-60px font-bold">
                Navigating the digital landscape for success
                </h1>
                <p className="heroText text-20px">Our digital marketing agency helps businesses grow and succeed online through a range of services including SEO, PPC, social media marketing, and content creation.</p>
                <button className="consultBtn  bg-black text-white text-20px">
                    Book a consultation
                </button>
            </div>
            <div>
                <img src={heroImg} alt="Hero Image" />
            </div>

        </div>

    </section>
)}

export default HeroSection;
